import { defineComponent, ref, reactive } from 'vue';
import { getworkerMessage } from '../../api/api.js';
const columns = [{
  title: '员工号',
  dataIndex: 'userId',
  width: '8%'
}, {
  title: '姓名',
  dataIndex: 'name',
  width: '8%'
}, {
  title: '公休总天数(2022年)',
  dataIndex: 'generalHolidayTotal',
  width: '10%'
}, {
  title: '公休剩余(天:时)',
  dataIndex: 'generalHolidayRemainder',
  width: '10%'
}, {
  title: '积休剩余(天:时)',
  dataIndex: 'accumulateHolidayRemainder',
  width: '10%'
}, {
  title: '积休已经用(天:时)',
  dataIndex: 'accumulateHolidayUsed',
  width: '15%'
}, {
  title: '值班加班换积休(天:时)',
  dataIndex: 'barterAccumulateHoliday',
  width: '15%'
}, {
  title: '总休息池(天:时)',
  dataIndex: 'restPoolTotal'
}];
export default defineComponent({
  setup() {
    let dataGroups = ref();
    let userId = ref();
    let name = ref();
    let getid = localStorage.getItem("Userid");
    getworkerMessage(getid).then(response => {
      if (response['code'] == 1) {
        name.value = response['data'].name;
        userId.value = response['data'].userId;
      }
    });
    return {
      name,
      userId,
      dataGroups
    };
  }

});
