import axiosInstance from './index'

const axios = axiosInstance

export const getBooks = () => {return axios.get('http://127.0.0.1:8000/api/attendance/')}

export const postBook = (bookName, bookAuthor) => {return axios.post('http://127.0.0.1:8000/api/books/', {'name': bookName, 'author': bookAuthor})}
