// import axiosInstance from './index'
import Axios from 'axios'
import Cookies from "js-cookie";


Axios.defaults.xsrfHeaderName = "X-CSRFToken";
Axios.defaults.xsrfCookieName = "csrftoken";

const BackEnd = "http://127.0.0.1:8000/"; //local backend from manage.py runserver

export default Axios.create({
  baseURL: `${BackEnd}api/`,
  timeout: 5000,
  headers: {
    "Content-Type": "application/json",
    "X-CSRFToken": Cookies.get('csrftoken')
  }
});


//CSRF拦截
// const axios = axiosInstance

const axios = Axios

export const getBooks = ()  => {return axios.get('http://127.0.0.1:8000/api/attendance/')}

export const postBook = (bookName, bookAuthor) => {return axios.post('http://127.0.0.1:8000/api/attendance/', {'name': bookName, 'author': bookAuthor})}

export const postSubmit = (username, password) => {return axios.post('http://127.0.0.1:8000/api/seeData/', {'name': username, 'author': password})}
