app.mount("#app");


router.beforeEach((to, from, next) => {

  //获取用户登录成功后储存的登录标志
  let getFlag = localStorage.getItem("Flag");
  let getid = localStorage.getItem("Userid");
  console.log('aaaaaaaaaaaaaaaaa',getFlag, getid)
  console.log('to:',to)
  console.log('from:',to)
  //如果登录标志存在且为isLogin，即用户已登录
  if (getFlag === "isLogin"&&getid !== null) {

      //设置vuex登录状态为已登录
      store.state.isLogin = true;
      next();

      //如果已登录，还想想进入登录注册界面，则定向回首页
      if (!to.meta.isLogin) {
          //iViewUi友好提示
          // iView.Message.error('请先退出登录');
          next({
              path: '/'
          })
      }

      //如果登录标志不存在，即未登录
  } else {

      //用户想进入需要登录的页面，则定向回登录界面
      if (to.meta.isLogin) {
          next({
              path: '/',
          })
          //用户进入无需登录的界面，则跳转继续
      } else {
          next()
      }

  }

});

router.afterEach(() => {
  window.scroll(0, 0);
});
