import { defineComponent, reactive, ref, nextTick, computed } from 'vue';
import { useStore } from 'vuex';
import { VXETable, VxeTableInstance, VxeToolbarInstance } from 'vxe-table';
export default defineComponent({
  setup() {
    const store = useStore();
    const serveApiUrl = computed(() => store.state.serveApiUrl);
    const xToolbar = ref({});
    const xTable = ref({});
    const demo1 = reactive({
      loading: false,
      tableData: [],
      validRules: {
        name: [{
          required: true,
          message: '名称必须填写'
        }]
      },
      sexList: [{
        label: '男',
        value: '1'
      }, {
        label: '女',
        value: '0'
      }]
    });

    const formatSex = value => {
      if (value === '1') {
        return '男';
      }

      if (value === '0') {
        return '女';
      }

      return '';
    };

    const loadList = async () => {
      demo1.loading = true;

      try {
        const res = await fetch(`${serveApiUrl.value}/api/pub/all`).then(response => response.json());
        demo1.tableData = res;
      } catch (e) {
        demo1.tableData = [];
      }

      demo1.loading = false;
    };

    const insertEvent = async () => {
      const $table = xTable.value;
      const newRecord = {};
      const {
        row: newRow
      } = await $table.insert(newRecord);
      await $table.setActiveRow(newRow);
    };

    const removeSelectEvent = async () => {
      const $table = xTable.value;
      await $table.removeCheckboxRow();
    };

    const deleteSelectEvent = async () => {
      const type = await VXETable.modal.confirm('您确定要删除选中的数据?');

      if (type !== 'confirm') {
        return;
      }

      const $table = xTable.value;
      const checkboxRecords = $table.getCheckboxRecords();
      demo1.loading = true;

      try {
        const body = {
          removeRecords: checkboxRecords
        };
        await fetch(`${serveApiUrl.value}/api/pub/save`, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify(body)
        });
        await loadList();
      } catch (e) {}

      demo1.loading = false;
    };

    const removeRowEvent = async row => {
      const $table = xTable.value;
      await $table.remove(row);
    };

    const deleteRowEvent = async row => {
      const type = await VXETable.modal.confirm('您确定要删除该数据?');

      if (type !== 'confirm') {
        return;
      }

      demo1.loading = true;

      try {
        const body = {
          removeRecords: [row]
        };
        await fetch(`${serveApiUrl.value}/api/pub/save`, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify(body)
        });
        await loadList();
      } catch (e) {}
    };

    const saveEvent = async () => {
      const $table = xTable.value;
      const {
        insertRecords,
        removeRecords,
        updateRecords
      } = $table.getRecordset();

      if (insertRecords.length <= 0 && removeRecords.length <= 0 && updateRecords.length <= 0) {
        VXETable.modal.message({
          content: '数据未改动！',
          status: 'warning'
        });
        return;
      }

      const errMap = await $table.validate();

      if (errMap) {
        return;
      }

      demo1.loading = true;

      try {
        const body = {
          insertRecords,
          removeRecords,
          updateRecords
        };
        await fetch(`${serveApiUrl.value}/api/pub/save`, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify(body)
        });
        await loadList();
        VXETable.modal.message({
          content: `操作成功，新增 ${insertRecords.length} 条，更新 ${updateRecords.length} 条，删除 ${removeRecords.length} 条`,
          status: 'success'
        });
      } catch (e) {
        if (e && e.message) {
          VXETable.modal.message({
            content: e.message,
            status: 'error'
          });
        }
      }

      demo1.loading = false;
    };

    nextTick(() => {
      // 将表格和工具栏进行关联
      const $table = xTable.value;
      const $toolbar = xToolbar.value;
      $table.connect($toolbar);
    });
    loadList();
    return {
      demo1,
      xToolbar,
      xTable,
      formatSex,
      insertEvent,
      removeSelectEvent,
      deleteSelectEvent,
      removeRowEvent,
      deleteRowEvent,
      saveEvent
    };
  }

});
