declare type EventFunc = () => void;
declare type ListenersSet = Set<EventFunc>;
declare type ListenerType = 'FOCUS_LISTENER' | 'VISIBLE_LISTENER' | 'RECONNECT_LISTENER';
export declare const FOCUS_LISTENER: ListenersSet;
export declare const VISIBLE_LISTENER: ListenersSet;
export declare const RECONNECT_LISTENER: ListenersSet;
declare const subscriber: (listenerType: ListenerType, event: EventFunc) => (() => void) | undefined;
export default subscriber;
